const BASE_URL = "http://localhost:3001";

export const fetcher = async (url) => {
  let r
  try{
    const response =  await fetch(BASE_URL + url);
    const responseData = await response.json();
    return responseData;
  }
  catch (error) {
    console.error("Error fetching data:", error);
    throw error;
  }
  
};
